import React, { useState } from "react";
import "./styles.css";

var emojiDictionary = {
  "ü•∞": "Smiling Face with Hearts",
  "üî•": "Fire",
  "üòä": "Smiling Face with Smiling Eyes",
  "ü•∫": "Pleading Face",
  "üòÇ": "Face with Tears of Joy",
  "üò•": "sad",
  "üòê": "Neutral Face",
  "üòâ": "Winking Face",
  "üíÄ": "extremely dangerous",
  "üëΩ": "Alien",
  "üéÉ": "Pumpkin lantern",
  "ü•°": "takeout box",
  "üëê": "affection and openness or hug",
  "üëè": "Clapping Hands",
  "üëç": "Well done!",
  "üëé": "rejection, disapproval and dislike",
  "‚úå": "Victory Hand",
  "üëå": "Ok Hand Sign",
  "‚úã": "Raised Hand",
  "üëã": "wave",
  "üí™": "strong",
  "üë£": "Footprints",
  "üíÉ": "dancing",
  "ü§õ": "Left-Facing Fist",
  "üëä": "Oncoming Fist"
};
var emojisWeKnow = Object.keys(emojiDictionary);

export default function App() {
  const [meaning, setMeaning] = useState("");

  function emojiInputHandler(event) {
    var userInput = event.target.value;

    var meaning = emojiDictionary[userInput];

    if (meaning === undefined) {
      meaning = "we dont have this in our database";
    }
    // console.log(meaning);
    setMeaning(meaning);
  }

  function emojiClickHandler(emoji) {
    // console.log(emoji);
    var meaning = emojiDictionary[emoji];
    setMeaning(meaning);
  }

  return (
    <div className="App">
      <h1> Emojis For </h1>
      <small>
        <i>smileys,people,Hand gestures,clothing</i>
      </small>
      <input
        placeholder="üîé click one from below"
        onChange={emojiInputHandler}
      ></input>
      <div>{meaning}</div>
      <h3>Emojis We Know</h3>
      <div className="emojiwrapper">
        {emojisWeKnow.map(function (emoji) {
          return (
            <span
              onClick={() => emojiClickHandler(emoji)}
              style={{
                fontSize: "xxx-large",
                cursor: "pointer"
                // padding: "1rem"
              }}
              key={emoji}
            >
              {emoji}
            </span>
          );
        })}
      </div>
    </div>
  );
}
